mapper.update:
update conference
set progress='ENDED'
where end_time<=#{currentTime};
update conference
set progress='OWNER_CONFIRM'
where progress='ENROLLMENT' and enroll_time<=#{currentTime};

update conference
set number=(select number from conference_number where id=#{id})
where id=#{id}

mapper.select:
select *
from conference
where progress<>'ENDED' and user_id=#{userId}

select *
from conference
where progress='ENDED' and user_id=#{userId}

select *
from conference
where user_id=#{userId} and id=#{id}

select *
from conference
where user_id=#{userId} and number=#{number}
